import './styles.css';
import { FaTrash } from "react-icons/fa";

function Card ( { transaction, setListTransactions } ) {

    const transactionDelete = () => {
        const excluir = transaction.filter((element) => {
          return element.description !== element.description
        })
        setListTransactions([...excluir])
        }  

    return (

        <div>

        {transaction.type === "Entrada" ?
            (
                <div className='Card-Entrada'>

                <div className='Des-Typ'>
                    <div className='Description'>
                        {transaction.description}
                    </div>

                    <div className='Type'>
                        {transaction.type}
                    </div>
                </div>

                <div className='Value'>
                    {Number(transaction.value).toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}
                    <FaTrash className="Trash"
                    onClick={transactionDelete}/>
                </div>

        </div>
            ) : (
                <div className='Card-Saida'>

                <div className='Des-Typ'>
                    <div className='Description'>
                        {transaction.description}
                    </div>

                    <div className='Type'>
                        {transaction.type}
                    </div>
                </div>

                <div className='Value'>
                    {Number(transaction.value).toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}
                    <FaTrash className="Trash"
                    onClick={transactionDelete}/>
                </div>

            </div>
            )
        }
        </div>
    )
}        

export default Card
