import { useState } from "react"
const Form = ({listTransactions, setListTransactions }) => {

    const [value, setValue]     = useState('')
    const [desc,   setDesc]     = useState('')
    const [type,   setType]     = useState('') 

    const setTransaction = () =>{

        if (type === 'Input'){
             return [...listTransactions, {description: desc, type: type, value: Number(value).toFixed(2) }]
        } else {
            return [...listTransactions, {description: desc, type: type, value: -Number(value).toFixed(2) }]
        }
    }

    return (
        <div className="form">
             <form >
        <label className='desc' >Description</label>
        <input 
        className="descriptionInput" placeholder="Your description"
        type='text' value={desc} onChange={(e) => setDesc(e.target.value)}>
        </input> 

        <label className='desc' >Value</label>
        <input 
        className='descriptionInput'
        value={value} type='number' placeholder="Your value"
        onChange={(e) =>setValue(e.target.value)}
        >
        </input>

        <label>Type</label>
         <select  value={type}  
         onChange={(e) => setType(e.target.value) }
         >
             <option value="Input"  >Input</option>
             <option value="Output" >Output</option>
         </select>

        <button className='sendValue' type="button" 
        onClick={() => setListTransactions(setTransaction)}
        >Send Transation</button>
      </form>   
    </div>
    )
}

export default Form
