    function Card({ transaction, listTransactions, SetListTransactions }) {
        const remove = (removeItem) => {
          let filtro = listTransactions.filter((itens) => {
            return itens !== removeItem;
          });
          console.log(filtro);
          SetListTransactions(filtro);
        };

        return (
          <div id="card" className={transaction.type}>
            <div className="controle3">
              <div className="linha1"></div>
              <div className="cardDescricao">
                <h3>{transaction.description}</h3>
                <p>{transaction.type}</p>
              </div>
            </div>
            <div className="controle4">
              <span>R$ {transaction.value.toFixed(2)}</span>
              <button
                className="lixo"
                onClick={() => remove(transaction)}
                type="button"
              >
              </button>
            </div>
          </div>
        );
      }

export default Card
