import "./style.css";
import { useState } from "react";

const Form = ({ listTransactions, setListTransactions }) => {
  const [newObj, setNewObj] = useState({});

  const createObj = (newKey) => {
    setNewObj({
      ...newObj,
      ...newKey,
    });
  };

  const addTransaction = (newTransaction) => {
    const newId =
      listTransactions.length > 0
        ? listTransactions[listTransactions.length - 1].id + 1
        : 0;
    const transactionObject = {
      ...newTransaction,
      id: newId,
    };
    if (
      newTransaction.description &&
      newTransaction.value &&
      newTransaction.type
    ) {
      setListTransactions([...listTransactions, transactionObject]);
    }
  };

  return (
    <section className="formulario">
      <div className="divTop">
        <h3>Descrição</h3>
        <input
          className="inputDescricao"
          placeholder="Digite aqui sua descrição"
          onChange={(e) => createObj({ description: e.target.value })}
        ></input>
        <span>Ex: Compra de roupas</span>
      </div>
      <div className="divBottom">
        <div>
          <h3>Valor</h3>
          <input
            type="number"
            placeholder="0"
            onChange={(e) => createObj({ value: Number(e.target.value) })}
          ></input>
        </div>
        <div>
          <h3>Tipo de valor</h3>
          <select onChange={(e) => createObj({ type: e.target.value })}>
            <option value="">Escolha o tipo</option>
            <option value="entrada">Entrada</option>
            <option value="despesa">Despesa</option>
          </select>
        </div>
      </div>
      <button onClick={() => addTransaction(newObj)}>Inserir valor</button>
    </section>
  );
};

export default Form;
