import { useState } from 'react';
import './App.css';
import Form from './components/Form';
import List from './components/List';
import TotalMoney from './components/TotalMoney';
import Header from './components/Header';

function App() {

  const [listTransactions, setListTransactions] = useState([])
  const [newListTransactions, setNewListTransactions] = useState()

  const filterEnters = () => {
    setNewListTransactions(listTransactions.filter((listTransactions) => {
      return listTransactions.type === "Entrada"
    }))
    }

  const filterExits = () => {
    setNewListTransactions(listTransactions.filter((listTransactions) => {
      return listTransactions.type === "Saida"
    }))
    }

    const filterAll = () => {
      setNewListTransactions(listTransactions.filter((listTransactions) => {
        return listTransactions.value =! 0
      }))
      }

  return (
    <div className="App">
      <header className="App-header">
        <div className='Header'>
          <Header/>
        </div>
        <div className="General"> 

          <div className='First-Colum'>
            { <Form 
            listTransactions={listTransactions}
            setListTransactions={setListTransactions}
            />}

            { <TotalMoney 
              listTransactions={listTransactions}
              setListTransactions={setListTransactions}
              />}
          </div>

          <div className='Colum-2'>
            <div className='Header-Colum-2'>
              <div className='Btns-Colum-2'>
                <p className='Res-Fin'>Resumo financeiro</p>
                  <button onClick={filterAll}>Todos</button>
                  <button onClick={filterEnters}>Entradas</button>
                  <button onClick={filterExits}>Despesas</button>
              </div>
              <div className="List">  
              {<List 
              listTransactions={listTransactions}
              />}
            </div> 
            </div> 
          </div>

        </div> 



      </header>
    </div>
  );
}

export default App;
