import "./styles.css"

function TotalMoney ( { listTransactions } ) {

    const totalMoney = listTransactions.reduce((previousValue, currentValue) =>
    {if(listTransactions.type === "Entrada") {
      return currentValue.value + previousValue
    }
      return currentValue.value - currentValue.value}, 0);

      return (
        <div className="TotalMoney">
          <p className="P-TotalMoney">Valor Total: </p>
          <span>R$ {totalMoney} </span>
        </div>
      );

}

export default TotalMoney
