import { useState } from 'react';
import './App.css';
import Form from './components/Form';
import List from './components/List';
import TotalMoney from './components/TotalMoney';
import Header from './components/Header';
import LandingPage from './components/LandingPage'

function App() {

  const [listTransactions, setListTransactions] = useState([])
  const [listNewTransactions, setNewListTransactions] = useState([])
  const [page, setNewPage] = useState(true)

  const iniciar = () => {
    if (page === true) {
        setNewPage(false)
    }
}

  const filterEnters = () => {
    const entradas = listTransactions.filter((transaction) => {
      return transaction.type === "Entrada"
    })
    setNewListTransactions([...entradas])
    }

  const filterExits = () => {
    const saidas = listTransactions.filter((transaction) => {
      return transaction.type === "Saida"
    })
    setNewListTransactions([...saidas])
    }

  const filterAll = () => {
    setNewListTransactions([])
  }

  return (

    <div className="App">
      {page === true ? (
        <div>
          <LandingPage/>
          <button onClick={iniciar}>Iniciar</button>
        </div>

      ) : (
        <header className="App-header">
        <div className='Header'>
          <Header/>
        </div>
        <div className="General"> 

          <div className='First-Colum'>
            { <Form 
            listTransactions={listTransactions}
            setListTransactions={setListTransactions}
            />}

            { <TotalMoney 
              listTransactions={listTransactions}
              listNewTransactions={listNewTransactions}
              setListTransactions={setListTransactions}
              />}
          </div>

          <div className='Colum-2'>
            <div className='Header-Colum-2'>
              <div className='Btns-Colum-2'>
                <p className='Res-Fin'>Resumo financeiro</p>
                  <button onClick={filterAll}>Todos</button>
                  <button onClick={filterEnters}>Entradas</button>
                  <button onClick={filterExits}>Despesas</button>
              </div>
              <div className="List">  
                {<List
                setListTransactions={setListTransactions}
                listTransactions={listNewTransactions.length > 0 ?
                  listNewTransactions : listTransactions}
              />}
              </div> 
            </div> 
          </div>

        </div> 



      </header>
      )}

    </div>
  );
}

export default App;
